      <Swiper
        ref={sliderRef}
        zoom={{ maxRatio: 2 }}
        spaceBetween={20}
        slidesPerView={3}
        breakpoints={{
          300: {
            slidesPerView: 1,
          },
          640: {
            slidesPerView: 1,
          },
          768: {
            slidesPerView: 2,
          },
          1024: {
            slidesPerView: 3,
          },
        }}
      >
        {comments.map((comment) => (
          <SwiperSlide key={comment.id}>
            <div className="swiper-zoom-container">
              <div className="cardp swiper-slide">
                <div className="image-content">
                  <div className="card-image">
                    <img
                      src={`https://portals.mentalland.com/image/users/comments/${comment.img}`}
                      alt="patient comment"
                      className="card-img patient"
                      loading="lazy"
                    />
                  </div>
                  <div className="patientName text-center">
                    {comment.Fname_Lname}
                  </div>
                  <div className="cu" style={{ whiteSpace: "nowrap" }}>
                    <span>{comment.date}</span>
                    <span>{comment.role}</span>
                  </div>
                </div>
                <div className="card-content">
                  <p className="comment text-center">{comment.description}</p>
                </div>
              </div>
            </div>
          </SwiperSlide>
        ))}
        <SwiperSlide>
          <div className="swiper-zoom-container">
            <div className="cardp swiper-slide">
              <div className="image-content">
                <div className="card-image">
                  <img
                    src={patientb}
                    alt="patient comment"
                    className="card-img patient"
                    loading="lazy"
                  />
                </div>
                <div className="patientName text-center">Benjamin Smith</div>
                <div className="cu">
                  <span>October 9</span>
                  <span>Painting</span>
                </div>
              </div>
              <div className="card-content">
                <p className="comment text-center">
                  Lorem ipsum dolor sit amet consectetur. Neque nam sed a sit
                  cras laoreet sed. Non felis lacus.
                </p>
              </div>
            </div>
          </div>
        </SwiperSlide></Swiper></div>
            </div>
          </div>
        ) : (
          <div className="explain">{content}</div>
        )}
      </section>
    </div>
  );
}
