import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { useTranslation } from "react-i18next";
import { RxHamburgerMenu } from "react-icons/rx";
import {MdKeyboardArrowDown} from "react-icons/md";


export default function Navbar({ menuName, menuItems, isOpen, onShow, onHide }) {
  return (
    <NavLink
      to={`/pages/${menuName.toLowerCase()}`}
      onMouseEnter={() => onShow(menuName)}
      onMouseLeave={() => onHide()}
    >
      {t(menuName)}
      {isOpen && (
        <div className="dropdown-content">
          <ul className="branch">
            {menuItems.map((item) => (
              <li key={item}>
                <NavLink to={`/pages/${item.toLowerCase()}`}>{t(item)}</NavLink>
              </li>
            ))}
          </ul>
        </div>
      )}
    </NavLink>
  );
}

function MyComponent() {
  const [openMenu, setOpenMenu] = useState(null);

  const showDropdown = (menuName) => {
    setOpenMenu(menuName);
  };

  const hideDropdown = () => {
    setOpenMenu(null);
  };

  return (
    <div>
      <DropdownMenu
        menuName="Psychology"
        menuItems={["Psychology"]}
        isOpen={openMenu === "Psychology"}
        onShow={showDropdown}
        onHide={hideDropdown}
      />
      <DropdownMenu
        menuName="Business"
        menuItems={["BusinessForAdults", "BusinessForChildren", "JobOpportunities"]}
        isOpen={openMenu === "Business"}
        onShow={showDropdown}
        onHide={hideDropdown}
      />
      <DropdownMenu
        menuName="Tourism"
        menuItems={["Tourism"]}
        isOpen={openMenu === "Tourism"}
        onShow={showDropdown}
        onHide={hideDropdown}
      />
    </div>
  );
}
