import React, { useState, useRef, useCallback } from "react";
import { useTranslation } from "react-i18next";
import Form from "react-bootstrap/Form";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, { Zoom, Navigation, Pagination } from "swiper";
import "swiper/modules/navigation/navigation";
import "swiper/modules/pagination/pagination";
import {
  IoIosArrowDroprightCircle,
  IoIosArrowDropleftCircle,
} from "react-icons/io";


export default function Explain( {data, comments, plan} ) {
  const [activeButton, setActiveButton] = useState(0);
  const { t, i18n } = useTranslation();
   const [key, setKey] = useState(0);

  const handleButtonClick = (index) => {
    setActiveButton(index);
  };

  const sliderRef = useRef(null);

  const handlePrev = useCallback(() => {
    if (!sliderRef.current) return;
    sliderRef.current.swiper.slidePrev();
  }, []);

  const handleNext = useCallback(() => {
    if (!sliderRef.current) return;
    sliderRef.current.swiper.slideNext();
  }, []);

   let content;
   if (activeButton === 0) {
     content = data.about_us;
   } else if (activeButton === 1) {
     content = comments.map((comment) => comment.answer_comment);
   } else if (activeButton === 2) {
     content = plan.map((item) => item.date + "" + item.time);
   }

  const activeClass = activeButton === 0 ? "active" : "";
  const activePlan = activeButton === 2 ? "active" : "";


  return (
    <div>
      <section className="knowing">
        {" "}
        <div className="knowme">
          <span
            className={
              activeClass +
              " " +
              (i18n.language === "fa" ? "persianMode" : "aboutme")
            }
            onClick={() => handleButtonClick(0)}
          >
            <span className="tx">{t("AboutMe")}</span>
          </span>
          <span
            className={activeButton === 1 ? "active comments" : "comments"}
            onClick={() => handleButtonClick(1)}
          >
            <span className="tx">{t("Comments")}</span>
          </span>
          <span
            className={
              activePlan +
              " " +
              (i18n.language === "fa" ? "rtlMode" : "weeklyplan")
            }
            onClick={() => handleButtonClick(2)}
          >
            <span className="tx">{t("WeeklyPlan")}</span>
          </span>
        </div>
        {activeButton === 2 ? (
          <div className="explain">
            {" "}
            <div className="timeplan">
              choose your time zone
              <Form>
                <Form.Select aria-label="wid" className="timezone"></Form.Select>
              </Form>
              <div key={key}>
      <div className="yyy">
        <h1 className="customersayH text-start">All Times are presented</h1>
        {i18n.language === "fa" ? (
          <div style={{ whiteSpace: "nowrap" }}>
            <IoIosArrowDroprightCircle
              className="swiper-navigation__prev"
              onClick={handlePrev}
            />
            <IoIosArrowDropleftCircle
              className="swiper-navigation__next"
              onClick={handleNext}
            />
          </div>
        ) : (
          <div style={{ whiteSpace: "nowrap" }}>
            <IoIosArrowDropleftCircle
              className="swiper-navigation__prev"
              onClick={handlePrev}
            />
            <IoIosArrowDroprightCircle
              className="swiper-navigation__next"
              onClick={handleNext}
            />
          </div>
        )}
      </div>
      <div className="customersay">
        Lorem ipsum dolor sit amet consectetur. Ut turpis nunc ac felis sapien
        diam. Sed velit dignissim viverra natoque. Ut mattis egestas{" "}
      </div>
      <Swiper
        ref={sliderRef}
        zoom={{ maxRatio: 2 }}
        spaceBetween={10}
        slidesPerView={3}
        breakpoints={{
          300: {
            slidesPerView: 2,
          },
          640: {
            slidesPerView: 3,
          },
          768: {
            slidesPerView: 4,
          },
          1024: {
            slidesPerView: 5,
          },
        }}
      >
        {comments.map((comment) => (
          <SwiperSlide key={comment.id}>

          </SwiperSlide>
        ))}
      </Swiper></div>
            </div>
          </div>
        ) : (
          <div className="explain">{content}</div>
        )}
      </section>
    </div>
  );
}
